from django import formsfrom django.contrib import adminfrom khovan.models import *from django.forms import ModelFormclass Editnhaphang(ModelForm):    class Meta:        model = Phieunhaphang        fields = '__all__'        widgets = {            'thoigiantao':forms.DateInput(attrs={'class':'form-control' }),            'code': forms.TextInput(attrs={'class': 'form-control','disabled':'true' }),            'thoigiannhanhang': forms.DateInput(attrs={'class': 'form-control'}),            'ghichu': forms.TextInput(attrs={'class': 'form-control'}),            'kho': forms.Select(attrs={'class': 'form-control'}),            'nhacungcap': forms.Select(attrs={'class': 'form-control'}),            'nhanvien': forms.TextInput(attrs={'class': 'form-control','id':'nhanvien','disabled':'true'}),        }class Nhaphangchuaduyetgaps(ModelForm):    class Meta:        model = Phieunhaphang        fields = ['tinhtrang','phanhoi','tuchoi']        widgets={            'phanhoi': forms.Textarea(attrs={'class': "form-control", 'rows':"3", 'cols':"3"}),        }    def __init__(self, *args, **kwargs):        super(Nhaphangchuaduyetgaps, self).__init__(*args, **kwargs)